00685 bool XLOGBASE::AddEntry(XLOGLEVEL level, XCHAR* sectionID, bool inmemory, XBYTE* data, XDWORD size, XDWORD sizeline, bool showoffset, bool showtext)
00686 {
00687   if(!IsActive()) return false;
00688   if(!mutex)      return false;
00689 
