00744 
00745               strdata.Format(__L("%c"),character);
00746               string += strdata;
00747               index++;
00748             }
00749         }
00750 
00751       mutex->UnLock();
00752 
00753       AddEntry(level, lines?NULL:sectionID, inmemory, string.Get());
00754 
