00866 
00867   entrys.DeleteContents();
00868   entrys.DeleteAll();
00869 
00870   XDWORD             sizeBOM              = 0;
00871   XFILETXTFORMATCHAR formatchar           = filelog->GetFormatCharFromFile(&sizeBOM);
00872   int                sizebytescharacter   = filelog->SizeOfCharacter(formatchar);
00873 
00874   xfile->SetPosition(sizeBOM);
00875 
00876   bool    endfile;
00877   XDWORD  br;
00878   XBUFFER dataline;
00879   XBYTE*  readbuffer = new XBYTE[XFILETXT_MAXBUFFER];
00880   if(!readbuffer) return false;
00881 
00882   memset(readbuffer, 0, XFILETXT_MAXBUFFER);
00883 
00884   do{ XDWORD  bufferpos = 0;
00885 
00886       br      = XFILETXT_MAXBUFFER;
00887       endfile = !xfile->Read(readbuffer, &br);
00888       if(!br) break;
00889 
00890       do{ XFILETXTTYPELF typeLF;
00891           XDWORD         sizeLF   = 0;
00892           XDWORD         sizeline = 0;
00893           bool           endline = filelog->GetSizeOfLine(formatchar, &readbuffer[bufferpos], typeLF, sizeLF, sizeline, (br-bufferpos));
00894 
00895           if(endline)
00896             {
00897               if(readbuffer[bufferpos] != 0x20)
00898                 {
00899                   nentrys++;
00900 
00901                   lastentry = new XLOGENTRY();
00902                   if(lastentry)
00903                     {
00904                       lastentry->position = bufferpos;
00905                       entrys.Add(lastentry);
00906                     }
00907                 }
00908 
00909               if(lastentry) lastentry->size += (sizeline + sizeLF);
00910               nlines++;
00911 
00912               sizeline  *= sizebytescharacter;
00913               sizeline  += (sizeLF*sizebytescharacter);
00914               bufferpos += sizeline;
00915             }
00916            else
00917             {
00918               int lack = (br-bufferpos);
00919               bufferpos += lack;
00920 
00921               if(endfile && (bufferpos == br))
00922                 {
00923                   if(readbuffer[bufferpos] != 0x20)
00924                     {
00925                       nentrys++;
00926 
00927                       lastentry = new XLOGENTRY();
00928                       if(lastentry)
00929                         {
00930                           lastentry->position = bufferpos;
00931                           entrys.Add(lastentry);
00932                         }
00933                     }
00934 
00935                   if(lastentry) lastentry->size += lack;
00936                   nlines++;
00937                 }
00938             }
00939 
00940         } while(bufferpos < br);
00941 
00942     } while(!endfile);
00943 
00944   delete [] readbuffer;
00945 
