01204    else
01205     {
01206       Backup_AdjustNFilesInCompressed();
01207     }
01208 
01209   if(iscompress)
01210     {
01211       XFILEZIP* xfilezip = new XFILEZIP();
01212       if(xfilezip)
01213         {
01214           XPATH xpathzipfile;
01215 
01216           Backup_CreateNameZIP(xpathzipfile);
01217 
01218           if(xfilezip->Open(xpathzipfile))
01219             {
01220               XPATH namefileinzip;
01221 
01222               xpathnamelog.GetNamefileExt(namefileinzip);
01223 
01224               if(!xfilezip->AddFile(xpathnamelog, namefileinzip)) status = false;
01225 
01226               xfilezip->Close();
01227             }
01228 
01229           delete xfilezip;
01230 
01231           status = xfilebackup->Erase(pathnamelog);
01232         }
01233     }
01234 
01235   GEN_XFACTORY.Delete_File(xfilebackup);
01236 
01237   return status;
01238 }
01239 
01240 
01250 bool XLOGBASE::Backup_ControlLimits()
01251 {
01252   if(!IsActive()) return false;
01253 
01254   if(!limit) return false;
01255 
01256   switch(typelimit)
01257     {
01258       case XLOGTYPELIMIT_NENTRYS  : if(nentrys<limit)   return false;
01259                                     break;
01260 
01261       case XLOGTYPELIMIT_SIZE     : if(size<limit)      return false;
01262                                     break;
01263       default: break;
01264     }
01265 
01266   Flush();
01267 
01268   bool status = true;
01269 
01270   if(backupisactive)
01271     {
01272       XPATH    xpathnamelog;
01273       XSTRING  datestr;
01274 
01275       //  If have backup DONT HAVE reduction
01276       //  The actual log file start
01277       xpathnamelog       = xpathfile.Get();
01278       backupxpathnamelog = xpathfile.Get();
01279 
01280       xdatetime->Read();
01281       datestr.Format(XLOG_CREATEMASKBACKUPFILE, xdatetime->GetYear()
01282                                               , xdatetime->GetMonth()
01283                                               , xdatetime->GetDay()
01284                                               , xdatetime->GetHours()
01285                                               , xdatetime->GetMinutes()
01286                                               , xdatetime->GetSeconds());
01287 
01288       backupxpathnamelog.AddToNameFile(true, datestr.Get());
01289 
01290       End(false);
01291 
01292       XFILE* GEN_XFACTORY_CREATE(xfile, Create_File())
01293       if(xfile)
01294         {
01295           xfile->Rename(xpathnamelog, backupxpathnamelog);
01296           GEN_XFACTORY.Delete_File(xfile);
01297         }
01298 
01299       status = Ini(xpathnamelog, NULL, isdumpintrace);
01300       if(status) status = Backup_AdjustNFiles(backupxpathnamelog.Get(), backupiscompress);
01301     }
01302    else
01303     {
01304       // Reduction control: NOT Backup.
01305 
01306       XDWORD nentrystoerase = nentrys - ((nentrys * reductionpercent) / 100);
01307       XQWORD sizetocopy = 0;
01308 
01309       for(int c=0; c<(int)nentrystoerase; c++)
01310         {
01311           XLOGENTRY* entry = entrys.Get(c);
01312           if(entry) sizetocopy += entry->size;
01313         }
01314 
01315       if(!sizetocopy) return false;
01316 
01317       filelog->GetPrimaryFile()->SetPosition(0);
01318       filelog->GetPrimaryFile()->EraseBlock(sizetocopy);
01319     }
01320 
01321   if(status) status = CalculateInitialStatus();
01322 
01323   return status;
01324 }
