01337 void XLOGBASE::Clean()
01338 {
01339   xdatetime        = NULL;
01340   xtimer           = NULL;
01341   mutex            = NULL;
01342   filelog          = NULL;
01343 
01344   isdumpintrace    = false;
01345 
01346   isactive         = false;
01347   typelimit        = XLOGTYPELIMIT_NONE;
01348 
01349   backupisactive   = false;
01350   backupmaxfiles   = 0;
01351   backupiscompress = false;
01352 
01353   limit            = 0;
01354   reductionpercent = XLOG_DEFAULTREDUCTIONPERCENT;
01355 
01356   lastsectionID.Empty();
01357 
01358   filtersectionsID.Empty();
01359   filterlevel      = XLOGLEVEL_ALL;
01360 
01361   nentrys          = 0;
01362   nlines           = 0;
01363   size             = 0;
01364   lastentry        = NULL;
01365 }
