00065 #define XMPINTEGER_MULADDC_INIT       \
00066                                       XLIMB s0, s1, b0, b1;                                               \
00067                                       XLIMB r0, r1, rx, ry;                                               \
00068                                       b0 = ( b << XMPINTEGER_HALFLIMBSIZE ) >> XMPINTEGER_HALFLIMBSIZE;   \
00069                                       b1 = ( b >> XMPINTEGER_HALFLIMBSIZE );
