00072 #define XMPINTEGER_MULADDC_CORE       \
00073                                       s0 = ( *s << XMPINTEGER_HALFLIMBSIZE ) >> XMPINTEGER_HALFLIMBSIZE;  \
00074                                       s1 = ( *s >> XMPINTEGER_HALFLIMBSIZE ); s++;                        \
00075                                       rx = s0 * b1; r0 = s0 * b0;                                         \
00076                                       ry = s1 * b0; r1 = s1 * b1;                                         \
00077                                       r1 += ( rx >> XMPINTEGER_HALFLIMBSIZE );                            \
00078                                       r1 += ( ry >> XMPINTEGER_HALFLIMBSIZE );                            \
00079                                       rx <<= XMPINTEGER_HALFLIMBSIZE; ry <<= XMPINTEGER_HALFLIMBSIZE;     \
00080                                       r0 += rx; r1 += (r0 < rx);                                          \
00081                                       r0 += ry; r1 += (r0 < ry);                                          \
00082                                       r0 +=  c; r1 += (r0 <  c);                                          \
00083                                       r0 += *d; r1 += (r0 < *d);                                          \
00084                                       c = r1; *(d++) = r0;
