01117 bool XPATH::Slash_Add()
01118 {
01119   int           count[2] = { 0 , 0 };
01120   XCHAR* str             = Get();
01121   bool          status   = false;
01122 
01123   XDWORD c = 0;
01124 
01125   while(c < GetSize())
01126     {
01127       if(str[c]==__C('/'))  count[0]++;
01128       if(str[c]==__C('\\')) count[1]++;
01129 
01130       c++;
01131     }
01132 
01133   if(!Slash_HaveAtLast())
01134     {
01135       if(count[0]>count[1])
01136              Add("/");
01137         else Add("\\");
01138 
01139       Slash_Normalize();
01140 
01141       status = true;
01142     }
01143 
01144   return status;
01145 }
