01159 bool XPATH::Slash_Normalize(bool inverse)
01160 {
01161   if(IsEmpty()) return false;
01162 
01163   XCHAR* str = Get();
01164   int           c   = 0;
01165 
01166   while(str[c])
01167     {
01168       if(inverse)
01169        {
01170          if(str[c]==__C('/'))  str[c] = __C('\\');
01171        }
01172       else
01173        {
01174          if(str[c]==__C('\\')) str[c] = __C('/');
01175        }
01176 
01177       c++;
01178     }
01179 
01180   return true;
01181 }
