01367 bool XPATH::Split(XSTRING* drive, XPATH* xpath, XSTRING* name, XSTRING* ext)
01368 {
01369   XCHAR* pathstr = Get();
01370   int    idrive  = 0;
01371   int    iname   = 0;
01372   int    iext    = 0;
01373   int    c;
01374 
01375   if(IsEmpty()) return false;
01376   if(!pathstr)  return false;
01377 
01378   c = 0;
01379   while(pathstr[c])
01380     {
01381       if(pathstr[c]==__C(':'))
01382         {
01383           if(drive) Copy(0,c+1,(*drive));
01384 
01385           idrive = c+1;
01386           break;
01387         }
01388 
01389       c++;
01390     }
01391 
01392   c = (GetSize()-1);
01393   while(pathstr[c])
01394     {
01395       if((pathstr[c]==__C('\\')) || (pathstr[c]==__C('/')))
01396         {
01397           break;
01398         }
01399 
01400       if(pathstr[c]==__C('.'))
01401         {
01402           if(ext) Copy(c, GetSize(), (*ext));
01403 
01404           iext = c;
01405           break;
01406         }
01407 
01408       c--;
01409     }
01410 
01411   c = (iext)?iext:GetSize();
01412   while(c>=0)
01413     {
01414       if((!c) || pathstr[c]==__C('\\') || pathstr[c]==__C('/')  || pathstr[c]==__C(':'))
01415         {
01416           if(pathstr[c]==__C('\\') || pathstr[c]==__C('/') || pathstr[c]==__C(':')) c++;
01417 
01418           if(name) Copy(c, (iext)?iext:GetSize(), (*name));
01419 
01420           iname = c;
01421           break;
01422         }
01423 
01424       c--;
01425     }
01426 
01427   if(xpath && iname) Copy(idrive, iname, (*xpath));
01428 
01429   return true;
01430 }
