00108 {   
00109   XPATH  pathstring;
00110   DIR    dir;
00111  
00112   pathstring = path;
00113   if(pathstring.IsEmpty()) return false; 
00114   
00115   pathstring.Slash_Delete();
00116   
00117   pathstring.Slash_Normalize(true);
00118   
00119   XBUFFER xbufferexchange;
