00371   searchelement->GetPathSearch()->Set(pathsearch_str);
00372     
00373   XSTM32DIR_SEARCHINFO* searchinfo = (XSTM32DIR_SEARCHINFO*)searchelement->GetFindFileData();    
00374   XBUFFER               xbufferexchange_pathsearch;
00375   XBUFFER               xbufferexchange_patternsearch;
