00377   pathsearch_str.ConvertToBufferExchange(xbufferexchange_pathsearch);
00378   patternsearch_str.ConvertToBufferExchange(xbufferexchange_patternsearch);
00379   
00380   fresult = f_findfirst(&searchinfo->dir, &searchinfo->fileinfo, xbufferexchange_pathsearch.Get(), xbufferexchange_patternsearch.Get());  
00381     
00382   if(fresult != FR_OK || !searchinfo->fileinfo.fname[0]) 
00383     {      
00384       searchelement->DeleteFindFileData();
00385       return false;
