00387 
00388   XSTRING searchfilename(searchinfo->fileinfo.fname);
00389 
00390   if((!searchfilename.Compare(__L(".")))||
00391      (!searchfilename.Compare(__L(".."))))
00392     {
00393       return NextSearch(searchelement);
