00461 bool XSTM32DIR::ReconvertSearchData(XDIRELEMENT* searchelement, FILINFO* fileinfo)
00462 {
00463   if(!searchelement)  return false;
00464   if(!fileinfo)       return false;
00465 
00466   searchelement->GetNameFile()->Set(fileinfo->fname);
00467      
00468   XDATETIME*  datetime  = searchelement->GetDateTimeFile_Modificated();
00469   XWORD       date      = fileinfo->fdate;
00470   XWORD       time      = fileinfo->ftime;
00471   
00472   
00473   // bit31:25    Year origin from the 1980 (0..127, e.g. 37 for 2017)
00474   // bit24:21    Month (1..12)
00475   // bit20:16    Day of the month (1..31)
00476     
00477   // bit15:11    Hour (0..23)
00478   // bit10:5     Minute (0..59)
00479   // bit4:0      Second / 2 (0..29, e.g. 25 for 50)
00480   
00481   datetime->SetYear(((date >> 9) & 0x7F) + 1980);
00482   datetime->SetMonth((date >> 5) & 0x0F);
00483   datetime->SetDay((date & 0x1F));
00484   
00485   datetime->SetHours((time >> 11) & 0x1F);
00486   datetime->SetMinutes((time >> 5) & 0x3F);
00487   datetime->SetSeconds((time & 0x1F) * 2);
00488     
00489   if(fileinfo->fattrib & AM_DIR)
00490           searchelement->SetType(XDIRELEMENTTYPE_DIR);
00491     else  searchelement->SetType(XDIRELEMENTTYPE_FILE);
00492  
00493   return true;
00494 }
