00468 bool XSCHEDULERTASK::CheckCondition(XDATETIME* xdatetimeactual, XTIMER* xtimeractual)
00469 {
00470   if(!xdatetimeactual) return false;
00471 
00472   XQWORD timeactualsec   = xdatetimeactual->GetSeconsFromDate();
00473 
00474   if(xdatetimelimitstart.IsValidDate())
00475     {
00476       if(timeactualsec <  xdatetimelimitstart.GetSeconsFromDate())
00477         {
00478           isinvalidtimelimit = false;
00479 
00480           xtimeractual->Reset();
00481           return false;
00482         }
00483        else isinvalidtimelimit = true;
00484     }
00485 
00486   if(xdatetimelimitend.IsValidDate())
00487     {
00488       if(timeactualsec >  xdatetimelimitend.GetSeconsFromDate())
00489         {
00490           isinvalidtimelimit = false;
00491           return false;
00492 
00493         } else isinvalidtimelimit = true;
00494     }
00495 
00496   if(conditiondayweekmask != XSCHEDULER_DAYWEEK_NONE)
00497     {
00498       if(!(GetDayOfWeekMask(xdatetimeactual) & conditiondayweekmask))
00499         {
00500           xtimeractual->Reset();
00501           return false;
00502         }
00503     }
00504 
00505   if(!ncyclesactual)  return false;
00506 
00507   bool checkcadence = true;
00508   if(isstartimmediatelycycles)
00509     {
00510       if(!ncyclesmade)  checkcadence = false;
00511     }
00512 
00513   if(checkcadence)
00514     {
00515       if(xdatetimecadence.IsValidDate())
00516         {
00517           if(xtimeractual->GetMeasureSeconds() < xdatetimecadence.GetSeconsFromDate()) return false;
00518         }
00519     }
00520 
00521   if(ncyclesactual>0)  ncyclesactual--;
00522 
00523   xtimeractual->Reset();
00524 
00525   ncyclesmade++;
00526 
00527   return true;
00528 }
