00542 XBYTE XSCHEDULERTASK::GetDayOfWeekMask(XDATETIME* xtimeactual)
00543 {
00544   if(!xtimeactual) return XSCHEDULER_DAYWEEK_NONE;
00545 
00546   XBYTE mask = XSCHEDULER_DAYWEEK_NONE;
00547 
00548   switch(xtimeactual->GetDayOfWeek())
00549     {
00550       case XDATETIME_DAYWEEK_SUNDAY     : mask = XSCHEDULER_DAYWEEK_ISSUNDAY;     break;
00551       case XDATETIME_DAYWEEK_MONDAY     : mask = XSCHEDULER_DAYWEEK_ISMONDAY;     break;
00552       case XDATETIME_DAYWEEK_TUESDAY    : mask = XSCHEDULER_DAYWEEK_ISTUESDAY;    break;
00553       case XDATETIME_DAYWEEK_WEDNESDAY  : mask = XSCHEDULER_DAYWEEK_ISWEDNESDAY;  break;
00554       case XDATETIME_DAYWEEK_THURSDAY   : mask = XSCHEDULER_DAYWEEK_ISTHURSDAY;   break;
00555       case XDATETIME_DAYWEEK_FRIDAY     : mask = XSCHEDULER_DAYWEEK_ISFRIDAY;     break;
00556       case XDATETIME_DAYWEEK_SATURDAY   : mask = XSCHEDULER_DAYWEEK_ISSATURDAY;   break;
00557     }
00558 
00559   return mask;
00560 }
