00652 bool XSCHEDULER::Ini()
00653 {
00654   GEN_XFACTORY_CREATE(xmutexscheduler, Create_Mutex())
00655   if(!xmutexscheduler) return false;
00656 
00657   GEN_XFACTORY_CREATE(xdatetimeactual, CreateDateTime())
00658   if(!xdatetimeactual) return false;
00659 
00660   xthreadscheduler = CREATEXTHREAD(XTHREADGROUPID_SCHEDULER, __L("XSCHEDULER::Ini"),ThreadScheduler,(void*)this);
00661   if(!xthreadscheduler) return false;
00662 
00663   if(!xthreadscheduler->Ini()) return false;
00664 
00665   isactive = true;
00666 
00667   return true;
