00790 XSCHEDULERTASK* XSCHEDULER::Task_GetForID(XDWORD ID)
00791 {
00792   if(!xmutexscheduler) return NULL;
00793   if(tasks.IsEmpty())  return NULL;
00794 
00795   xmutexscheduler->Lock();
00796 
00797   for(XDWORD c=0;c<tasks.GetSize();c++)
00798     {
00799       XSCHEDULERTASK* task = tasks.Get(c);
00800       if(task)
00801         {
00802           if(task->GetID()==ID)
00803             {
00804               xmutexscheduler->UnLock();
00805               return task;
00806             }
00807         }
00808     }
00809 
00810   xmutexscheduler->UnLock();
00811 
00812   return NULL;
00813 }
