00922 bool XSCHEDULER::End()
00923 {
00924   isactive = false;
00925 
00926   if(xthreadscheduler)
00927     {
00928       xthreadscheduler->End();
00929       DELETEXTHREAD(XTHREADGROUPID_SCHEDULER, xthreadscheduler);
00930 
00931       xthreadscheduler = NULL;
00932     }
00933 
00934   Task_DelAll();
00935 
00936   if(xmutexscheduler)
00937     {
00938       GEN_XFACTORY.Delete_Mutex(xmutexscheduler);
00939       xmutexscheduler = NULL;
00940     }
00941 
00942   if(xdatetimeactual)
00943     {
00944       GEN_XFACTORY.DeleteDateTime(xdatetimeactual);
00945       xdatetimeactual = NULL;
00946     }
00947 
00948   return true;
00949 }
