00963 void XSCHEDULER::ThreadScheduler(void* data)
00964 {
00965   XSCHEDULER* xscheduler = (XSCHEDULER*)data;
00966   if(!xscheduler)                      
00967     {
00968       return;
00969     }
00970 
00971   if(!xscheduler->xmutexscheduler)     
00972     {
00973       return;
00974     }
00975 
00976   if(!xscheduler->xdatetimeactual)     
00977     {
00978       return;
00979     }
00980 
00981   if(!xscheduler->isactive) 
00982     {
00983       return;
00984     }
00985 
00986   GEN_XSLEEP.MilliSeconds(10);
00987 
00988   xscheduler->xmutexscheduler->Lock();
00989 
00990   XSCHEDULERTASK* task;
00991 
00992   task = xscheduler->Task_Get(xscheduler->indextask);
00993   if(!task)
00994     {
00995       xscheduler->xmutexscheduler->UnLock();
00996       xscheduler->indextask = 0;
00997       return;
00998     }
00999 
01000   if(xscheduler->xdatetimeactual) 
01001     {
01002       xscheduler->xdatetimeactual->Read();
01003     }
01004 
01005   if(task->IsActive())
01006     {
01007       if(task->CheckCondition(xscheduler->xdatetimeactual, task->GetXTimer()))
01008         {
01009           xscheduler->indextask++;
01010           xscheduler->xmutexscheduler->UnLock();
01011 
01012           XSCHEDULER_XEVENT xevent(xscheduler, XEVENT_TYPE_SCHEDULER);
01013 
01014           xevent.SetScheduler(xscheduler);
01015           xevent.SetTask(task);
01016           xevent.SetDateTime(xscheduler->xdatetimeactual);
01017 
01018           xscheduler->PostEvent(&xevent);
01019                  
01020           return;
01021         }
01022     }
01023    
01024   xscheduler->indextask++;
01025   xscheduler->xmutexscheduler->UnLock();
01026 }
