00428 bool XSERIALIZATIONMETHODJSON::AddStruct(XCHAR* name, bool open)
00429 {
00430   if(!CheckHandleActive()) 
00431     {
00432       return false;
00433     }
00434 
00435   if(open)
00436     {
00437       XFILEJSONOBJECT* structnode =  new XFILEJSONOBJECT();
00438       if(!structnode)
00439         {
00440           return false;
00441         }
00442 
00443       fathers.Add(GetActualObject());      
00444       SetActualObject(structnode);
00445     }
00446    else
00447     {
00448       fathers.GetLast()->Add(name, (XFILEJSONOBJECT*)GetActualObject());  
00449       SetActualObject(fathers.GetLast());      
00450       fathers.DeleteLast();
00451     }
00452 
00453   return true;
00454 }
