01324 bool XSTRING::DeleteCharacters(XDWORD index, XDWORD ncharacteres)
01325 {
01326   if(!ncharacteres)  return true;
01327   if(IsEmpty())      return false;
01328 
01329   if(index >= size)  return false;
01330 
01331   int nchar = ncharacteres;
01332   if((index + nchar) > size) nchar = (int)size-index;
01333 
01334   int c = index;
01335   while(text[c+nchar])
01336     {
01337       text[c] = text[c+nchar];
01338       c++;
01339     }
01340   text[c] = text[c + nchar];
01341 
01342   return ReAllocBuffer(size - nchar);
01343 }
