01389 bool XSTRING::DeleteCharacter(XCHAR character,XSTRINGCONTEXT context)
01390 {
01391   if(IsEmpty()) return false;
01392 
01393   switch(context)
01394     {
01395       case XSTRINGCONTEXT_FROM_FIRST  : { XDWORD n=0;
01396                                           while(character == text[n] && n<size)
01397                                             {
01398                                               n++;
01399                                             }
01400                                           if(n) DeleteCharacters(0,n);
01401                                         }
01402                                         break;
01403 
01404       case XSTRINGCONTEXT_TO_END      : { XDWORD n=size-1;
01405                                           while(character == text[n] && n)
01406                                             {
01407                                               n--;
01408                                             }
01409 
01410                                           if(size!=n+1)  ReAllocBuffer(n+1);
01411                                         }
01412                                         break;
01413 
01414       case XSTRINGCONTEXT_ALLSTRING   : { XDWORD c = 0;
01415                                           XDWORD a = 0;
01416 
01417                                           while (c < size)
01418                                             {
01419                                               while(text[c]==character)
01420                                                 {
01421                                                   c++;
01422                                                 }
01423 
01424                                               text[a] = text[c];
01425                                               c++;
01426 
01427                                               if(!text[a]) break;
01428                                               a++;
01429                                             }
01430 
01431                                           ReAllocBuffer(a);
01432                                           size = a;
01433                                         }
01434                                         break;
01435     }
01436 
01437   return true;
01438 }
