01512                                             for(n = size- start-1; n >=0; n--)
01513                                               {
01514                                                 XCHAR character = text[n];
01515                                                 bool found = false;
01516 
01517                                                 for(XDWORD e = 0;  needle.Get()[e] != __C('\0'); e++)
