01524                                                   }
01525 
01526                                                 if(!found) break;
01527                                               }
01528                                             this->DeleteCharacters(n+1, size -1 - n);
01529                                             return n;
01530                                           }
01531                                           //break;
01532 
01533       case XSTRINGCONTEXT_ALLSTRING     : { for(XDWORD n = start; n < size; n++)
01534                                               {
01535                                                 XCHAR character = text[n];
01536                                                 bool found = false;
01537 
01538                                                 for(XDWORD e=0; needle.Get()[e] != __C('\0'); e++)
01539                                                   {
