01596   if(index <= 0) 
01597     {
01598       return false;
01599     }
01600 
01601   while(Character_GetLast () == __C('0'))
01602     {
01603       DeleteLastCharacter();
01604       status = true;
01605     }
01606 
01607   return status;
01608 }
01609 
01610 
01622 bool XSTRING::Character_IsAlpha(XCHAR character) const
01623 {
01624   XBYTE _char = (XBYTE)character;
01625 
01626   if(((_char>='a') && (_char<='z')) ||
01627      ((_char>='A') && (_char<='Z'))) return true;
01628 
01629   return false;
01630 }
