01674 
01686 bool XSTRING::Character_IsNumber(XCHAR character)
01687 {  
01688   if((character >= __C('0')) && (character <= '9'))  return true;
01689 
01690   if(character == __C('.')) return true;
01691   if(character == __C(',')) return true;
01692   if(character == __C('-')) return true;
01693   if(character == __C('E')) return true;
01694   if(character == __C('+')) return true;
01695 
01696   return false;
01697 }
01698 
01699 
01711 bool XSTRING::Character_IsNOT(XCHAR character)
01712 {
01713   switch(character)
01714     {
01715       case 0x0000: return true;
01716       case 0x0009: return true; // Tab
01717       case 0x000A: return true; // Return
01718       case 0x000D: return true; // Return
01719       case 0x0020: return true; // Space
01720     }
01721 
01722   return false;
01723 }
