01725 
01737 XCHAR XSTRING::Character_ToUpper(XCHAR character) const
01738 {
01739 
01740   XCHAR _character = character;
01741   XBYTE _char      = (XBYTE)_character;
01742 
01743   if((_char>='a')&&(_char<='z'))
01744     {
01745        _char      -= 32;
01746        _character  = (XCHAR)_char;
01747     }
01748 
01749   return _character;
01750 }
01751 
01752 
01764 XCHAR XSTRING::Character_ToLower(XCHAR character) const
01765 {
01766   XCHAR _character = character;
01767   XBYTE _char      = (XBYTE)_character;
01768 
01769   if((_char>='A')&&(_char<='Z'))
01770     {
01771        _char      += 32;
01772        _character  = (XCHAR)_char;
01773     }
01774 
01775   return _character;
01776 }
