01795 
01805 XCHAR XSTRING::Character_GetLast()
01806 {
01807   int c;
01808 
01809   if(IsEmpty()) return 0;
01810 
01811   for(c=0;c<(int)size;c++)
01812     {
01813       if(!text[c]) break;
01814     }
01815 
01816   if((c-1) >=0) return text[c-1];
01817 
01818   return 0;
01819 }
