01886 bool XSTRING::IsDecimalNumber(int* ndecimals)
01887 {
01888   if(ndecimals)
01889     {
01890       (*ndecimals) = 0;
01891     }
01892 
01893   if(IsEmpty()) 
01894     {
01895       return false;
01896     }
01897 
01898   if(!IsNumber())
01899     {
01900       return false;
01901     }
01902   
01903   bool found = false;
01904 
01905   for(XDWORD c=0;c<size;c++)
01906     {      
01907       if((text[c] == __C('.')) || (text[c] == __C(',')))
01908         {          
01909           (*ndecimals) = (size - c);
01910 
01911           found = true;
01912           break;
01913         }
01914     }
01915 
01916   return found;
01917 }
