01971 
01985 int XSTRING::Compare(const XCHAR* string, int csize, bool ignorecase)
01986 {
01987   XDWORD stringsize=csize;
01988 
01989   if(size!=stringsize)
01990     {
01991       if(size>stringsize) return  1;
01992       if(size<stringsize) return -1;
01993     }
01994 
01995   if(!size && !stringsize) return 0;
01996 
01997   XCHAR* _text = (XCHAR*)string;
01998 
01999   if(ignorecase)
02000     {
02001       for(int c=size-1;c>=0;c--)
02002         {
02003           XCHAR a=_text[c];
02004           XCHAR b=text[c];
02005 
02006           if(Character_IsAlpha(a))
02007             {
02008               XCHAR c1 = Character_ToUpper(a);
02009               XCHAR c2 = Character_ToUpper(b);
02010 
02011               if(c1 == c2) continue;
02012               if(c1 < c2) return  1;
02013               if(c1 > c2) return -1;
02014             }
02015            else
02016             {
02017               if(a == b)  continue;
02018               if(a < b)   return  1;
02019               if(a > b)   return -1;
02020             }
02021         }
02022     }
02023    else
02024     {
02025       for(int c=size-1;c>=0;c--)
02026         {
02027           if(_text[c] == text[c]) continue;
02028           if(_text[c] < text[c])  return  1;
02029           if(_text[c] > text[c])  return -1;
02030         }
02031     }
02032 
02033   return 0;
02034 }
