02225 
02239 int XSTRING::Find(XSTRING& string, bool ignorecase,int startindex)
02240 {
02241   if(IsEmpty())                      return XSTRING_NOTFOUND;
02242   if(string.IsEmpty())               return XSTRING_NOTFOUND;
02243 
02244   XCHAR* _text = string.Get();
02245   XDWORD _size = string.GetSize();
02246 
02247   if(_size > size)                    return XSTRING_NOTFOUND;
02248   if(startindex>=(int)(size-_size)+1) return XSTRING_NOTFOUND;
02249   if(startindex<0)                    return XSTRING_NOTFOUND;
02250 
02251   bool found;
02252 
02253   for(XDWORD c=startindex;c<(size-_size)+1;c++)
02254     {
02255       found = true;
02256 
02257       for(XDWORD d=0;d<_size;d++)
02258         {
02259           if(ignorecase && Character_IsAlpha(_text[d]))
02260             {
02261               XCHAR c1 = Character_ToUpper(_text[d]);
02262               XCHAR c2 = Character_ToUpper(text[c+d]);
02263 
02264               if(c1!=c2)
02265                 {
02266                   found = false;
02267                   break;
02268                 }
02269             }
02270            else
02271             {
02272               if(_text[d]!=text[c+d])
02273                 {
02274                   found = false;
02275                   break;
02276                 }
02277             }
02278         }
02279 
02280       if(found) return c;
02281     }
02282 
02283   return XSTRING_NOTFOUND;
02284 }
02285 
02286 
02300 int XSTRING::FindCharacter(XCHAR character, XDWORD startindex, bool endtostart)
02301 {
02302   if(startindex >= (XDWORD)size)  return XSTRING_NOTFOUND;
02303 
02304   if(endtostart)
02305     {
02306       for(int c=size-startindex-1; c>=0; c--)
02307         {
02308           if(text[c]==character) return c;
02309         }
02310     }
02311    else
02312     {
02313       for(XDWORD c=startindex; c<size; c++)
02314         {
02315           if(text[c] == character) return c;
02316         }
02317     }
02318 
02319   return XSTRING_NOTFOUND;
02320 }
