02342   if(startindex<0)          return 0;
02343 
02344   if(endtostart)
02345     {
02346       for(c=size-startindex; c>0; c--)
02347         {
02348           if(text[c]==character) count++;
02349         }
02350     }
02351    else
02352     {
02353       for(c=startindex; c<size; c++)
02354         {
02355           if(text[c]==character) count++;
02356         }
02357     }
02358 
02359   return count;
02360 }
