02437 bool XSTRING::AreValidCharacters(XCHAR* validcharacterslist)
02438 {
02439   if(!validcharacterslist) return false;
02440 
02441   XSTRING vcl;
02442 
02443   vcl = validcharacterslist;
02444 
02445   for(int d=0; d<(int)GetSize(); d++)
02446     {
02447       bool isvalid = false;
02448 
02449       for(int c=0; c<(int)vcl.GetSize(); c++)
02450         {
02451           if(Get()[d] == vcl.Get()[c])
02452             {
02453               isvalid = true;
02454               break;
02455             }
02456         }
