02464 
02480 int XSTRING::Copy(const XCHAR* startmark, const XCHAR* endmark, bool ignorecase, int addstartindex, XSTRING& string)
02481 {
02482   int startindex  = 0;
02483   int endindex    = GetSize();
02484   XDWORD sizeendmark = 0;
02485 
02486   string.Empty();
02487 
02488   if(startmark)
02489     {
02490       XSTRING mark;
02491 
02492       mark = startmark;
02493       startindex = Find(startmark, ignorecase, addstartindex);
02494       if(startindex==XSTRING_NOTFOUND) return XSTRING_NOTFOUND;
02495 
02496       startindex+= mark.GetSize();
02497     }
02498 
02499   if(endmark)
02500     {
02501       XSTRING mark;
02502 
02503       mark = endmark;
02504       endindex = Find(endmark, ignorecase, startindex);
02505       if(endindex==XSTRING_NOTFOUND) endindex = size;
02506 
02507       sizeendmark = mark.GetSize();
02508     }
02509 
02510   for(int c=startindex;c<endindex;c++)
02511     {
02512       XCHAR str[2] = { text[c], 0 };
02513 
02514       string+=str;
02515     }
02516 
02517   int indexnext = (endindex+sizeendmark);
02518 
02519   if(indexnext>(int)size) indexnext = (int)size;
02520 
02521   return indexnext;
02522 }
