02665 
02678 int XSTRING::Replace(XCHAR* findwhat, XCHAR* replaceby)
02679 {
02680   XSTRING what;
02681   XSTRING replace;
02682 
02683   what.Set(findwhat);
02684 
02685   replace.Set(replaceby);
02686 
02687   int found;
02688   int coincidences = 0;
02689 
02690   do{ found = ReplaceFirst(what.Get(),replace.Get());
02691 
02692       if (found!=XSTRING_NOTFOUND)
02693         coincidences++;
02694       else
02695         break;
02696     } while(1);
02697 
02698   return coincidences;
02699 }
02700 
02701 
02714 int XSTRING::ReplaceFirst(XCHAR* findwhat, XCHAR* replaceby)
02715 {
02716   XSTRING what;
02717   XSTRING replace;
02718 
02719   what.Set(findwhat);
02720   replace.Set(replaceby);
02721 
02722   int whatsize = what.GetSize();
02723   int found    = 0;
02724 
02725   found = Find(findwhat, false, found);
02726   if(found != XSTRING_NOTFOUND)
02727     {
02728       DeleteCharacters(found, whatsize);
02729       Insert(replace, found);
02730     }
02731 
02732   return found;
02733 }
02734 
02735 
02748 bool XSTRING::Insert(XSTRING str,XDWORD position)
02749 {
02750   XSTRING tmp;
02751 
02752   if(position > size)
02753     {
02754       return this->Add(str);
02755     }
02756 
02757   Copy(0,position,tmp);
02758 
02759   tmp.Add(str);
02760 
02761   XSTRING trail;
02762 
02763   Copy(position, trail);
02764   tmp.Add(trail);
02765 
02766   Set(tmp);
02767 
02768   return true;
02769 }
