02825 bool XSTRING::ConvertFromShort(short value, const XCHAR* mask)
02826 {
02827   char*     str;
02828   XSTRING  _mask;
02829 
02830   str = new char[XSTRING_MAXTEMPOSTR];
02831   if(!str) return false;
02832 
02833   memset(str,0,XSTRING_MAXTEMPOSTR);
02834 
02835   if(mask) _mask = mask; else _mask = __L("%hd");
02836 
02837   XBUFFER charstr;
02838   
02839   _mask.ConvertToASCII(charstr);
02840   SPRINTF(str, charstr.GetPtrChar(), value);
02841   
02842   Set(str);
02843 
02844   delete [] str;
02845 
02846   return true;
02847 }
