02903 
02904   str = new char[XSTRING_MAXTEMPOSTR];
02905   if(!str) return false;
02906 
02907   memset(str,0,XSTRING_MAXTEMPOSTR);
02908 
02909   if(mask) _mask = mask; else _mask = __L("%d");
02910 
02911 
02912   XBUFFER charstr;
02913   
02914   _mask.ConvertToASCII(charstr);
02915   SPRINTF(str, charstr.GetPtrChar(), value);
02916   
02917   Set(str);
02918 
02919   delete [] str;
02920 
02921   return true;
02922 }
