03119 
03129 bool XSTRING::ConvertHexFormatChars()
03130 {
03131   XSTRING string;
03132 
03133   string.Empty();
03134 
03135   if(IsEmpty()) return false;
03136 
03137   for(int c=0;c<(int)GetSize();c++)
03138     {
03139       XCHAR character[3] = { 0 , 0 , 0 };
03140 
03141       character[0] = Get()[c];
03142       character[1] = Get()[c+1];
03143 
03144       if((character[0]==__C('\\')) && (character[1]==__C('x')))
03145         {
03146           XSTRING   numberhex;
03147           XCHAR part[2] = { 0 , 0 };
03148 
03149           c+=2;
03150 
03151           for(int d=0;d<2;d++)
03152             {
03153               part[0] = Get()[c+d];
03154               numberhex.Add(part);
03155             }
03156 
03157           numberhex.UnFormat(__L("%02X"),&part[0]);
03158 
03159           part[0]&=0x00FF;
03160           string.Add(part);
03161 
03162           c++;
03163         }
03164        else string.Add((XCHAR)character[0]);
03165     }
03166 
03167   if(string.IsEmpty()) return false;
03168 
03169   Set(string);
03170 
03171   return true;
03172 }
03173 
03174 
03187 bool XSTRING::ConvertToHexString(XSTRING& string, bool uppercase)
03188 {
03189   XSTRING datastr;
03190 
03191   string.Empty();
03192 
03193   for(int c=0;c<(int)GetSize(); c++)
03194     {
03195       datastr.Format(uppercase?__L("%02X"):__L("%02x"), Get()[c]);
03196       string.Add(datastr);
03197     }
03198 
03199   return true;
03200 }
03201 
03202 
03214 bool XSTRING::ConvertHexStringToBuffer(XBUFFER& xbuffer)
03215 {
03216   xbuffer.Delete();
03217 
03218   for(int c=0; c<(int)GetSize(); c+=2)
03219     {
03220       XCHAR    hexbytechar[3] = { 0, 0, 0 };
03221       XSTRING  hexbytestr;
03222       int      data;
03223 
03224       hexbytechar[0] = Get()[c];
03225       hexbytechar[1] = Get()[c+1];
03226 
03227       hexbytestr = hexbytechar;
03228 
03229       hexbytestr.UnFormat(__L("%02X"), &data);
03230       xbuffer.Add((XBYTE)data);
03231     }
03232 
03233   return true;
03234 }
03235 
