03252 
03253   for(int c=0; c<(int)xbuffer.GetSize(); c++)
03254     {
03255       XSTRING  hexbytestr;
03256 
03257       hexbytestr.Format(uppercase?__L("%02X"):__L("%02x"), xbuffer.Get()[c]);
03258       Add(hexbytestr);
03259     }
03260 
