03264 
03274 bool XSTRING::ConvertToBoolean()
03275 {
03276   bool result = false;
03277 
03278   if(!Compare(__L("yes")  , true)  || 
03279      !Compare(__L("true") , true)  || 
03280      !Compare(__L("YES")  , true)  || 
03281      !Compare(__L("TRUE") , true))
03282     {
03283       result = true;
03284     }
03285    else
03286     {
03287       if(!Compare(__L("no")    , true)    || 
03288          !Compare(__L("false") , true)    || 
03289          !Compare(__L("NO")    , true)    || 
03290          !Compare(__L("FALSE") , true))
03291         {
03292           result = false;
03293         }
03294        else
03295         {
03296           if(Compare(__L("0")  , true)) 
03297             {
03298               result = true;
03299             }
03300         }
03301     }
03302 
03303   return result;
03304 }
