03320 int XSTRING::ConvertToInt(int index, const XCHAR* mask, bool checkvalidchars)
03321 {
03322   if(IsEmpty()) return 0;
03323 
03324   if(checkvalidchars)
03325     {
03326       XSTRING  validcharacters;
03327       validcharacters  = XSTRING_VALIDCHARSINT;
03328       if(!AreValidCharacters(validcharacters.Get())) return 0;
03329     }
03330 
03331   XSTRING  _mask;
03332   int      data = 0;
03333 
03334   if(mask)
03335          _mask = mask;
03336     else _mask = __L("%d");
03337     
03338     
03339   XBUFFER charstr;
03340   XBUFFER charstr2;
03341   
03342   _mask.ConvertToASCII(charstr);     
03343   (*this).ConvertToASCII(charstr2);         
03344   
03345   SSCANF(&charstr2.GetPtrChar()[index], charstr.GetPtrChar(), &data);
03346  
03347   return data;
03348 }
