03555 
03565 bool XSTRING::IsValidASCII()
03566 {
03567   for(int c=0;c<(int)size;c++)
03568     {
03569       if((text[c]<0x20) || (text[c]>0x7F)) return false;
03570     }
03571 
03572   return true;
03573 }
03574 
03575 
03587 bool XSTRING::ConvertToASCII(XBUFFER& xbuffer)
03588 {   
03589   xbuffer.Empty();
03590 
03591   for(XDWORD c=0; c<size; c++)
03592     {    
03593       if(text[c]<0x80) 
03594         {                    
03595           xbuffer.Add((XBYTE)(text[c]));
03596         }
03597        else
03598         {
03599           xbuffer.Add((XBYTE)'_');
03600         } 
03601     }
03602 
03603   xbuffer.Add((XBYTE)0x00);
03604 
03605   return true;
03606 }
03607     
03608 
03620 bool XSTRING::ConvertFromASCII(XBUFFER& xbuffer)
03621 {
03622   if(xbuffer.IsEmpty()) return false;
03623 
03624   Empty();
03625 
03626   for(XDWORD c=0; c<xbuffer.GetSize()-1; c++)
03627     {
03628       XCHAR character = (XCHAR)(xbuffer.Get()[c]);
03629 
03630       Add((XCHAR)character);      
03631     }
03632   
03633   return true;
03634 }
