03636 
03648 bool XSTRING::ConvertToXBuffer(XBUFFER& xbuffer)
03649 {
03650   bool status = false;
03651 
03652   if(IsEmpty()) return size;
03653 
03654   xbuffer.Empty();
03655 
03656   switch(GetTypeCoding())
03657     {
03658       case XSTRINGCODING_UTF16  : { for(XDWORD c=0; c<size; c++)
03659                                       {
03660                                         xbuffer.Add((XWORD)text[c]);
03661                                       }
03662                                   
03663                                     status = true;  
03664                                   }
03665                                   break;  
03666 
03667       case XSTRINGCODING_UTF32  : { for(XDWORD c=0; c<size; c++)
03668                                       {
03669                                         xbuffer.Add((XDWORD)text[c]);
03670                                       }
03671                                   
03672                                     status = true;  
03673                                   }
03674                                   break;  
03675     }
03676 
03677   return status;
03678 }
03679 
03680 
03693 bool XSTRING::ConvertFromXBuffer(XBUFFER& xbuffer, XSTRINGCODING buffercoding)
03694 {  
03695   bool status = false;
03696 
03697   if(xbuffer.IsEmpty()) return false;
03698 
03699   Empty();
03700 
03701   switch(GetTypeCoding())
03702     {
03703       case XSTRINGCODING_UTF16  : switch(buffercoding)
03704                                     {
03705                                       case XSTRINGCODING_UNKWOWN  : { xbuffer.ResetPosition();
03706 
03707                                                                       for(XDWORD c=0; c<xbuffer.GetSize(); c++)
03708                                                                         {
03709                                                                           XBYTE character;
03710 
03711                                                                           xbuffer.Get(character);
03712                                                                           Add((XCHAR)character);
03713                                                                         }                                                                                                        
03714                                                                     }
03715                                                                     status = true; 
03716                                                                     break;
03717 
03718                                       case XSTRINGCODING_UTF8     : ConvertFromUTF8(xbuffer);
03719                                                                     status = true;
03720                                                                     break;
03721 
03722                                       case XSTRINGCODING_UTF16    : { xbuffer.ResetPosition();
03723 
03724                                                                       for(XDWORD c=0; c<xbuffer.GetSize(); c++)
03725                                                                         {
03726                                                                           XWORD character;
03727 
03728                                                                           xbuffer.Get(character);
03729                                                                           Add((XCHAR)character);
03730                                                                         }                                                                                                        
03731                                                                     }
03732                                                                     status = true;  
03733                                                                     break;
03734 
03735                                       case XSTRINGCODING_UTF32    : { xbuffer.ResetPosition();
03736 
03737                                                                       for(size_t c=0; c<xbuffer.GetSize(); c++) 
03738                                                                         {
03739                                                                           XDWORD value = 0;
03740 
03741                                                                           xbuffer.Get(value);
03742                                                                           if (value > 0xFFFF) 
03743                                                                             {
03744                                                                               value -= 0x10000;
03745 
03746                                                                               XDWORD lo = ((XWORD)(0xD800 | (value >> 10)));   
03747                                                                               XDWORD hi = ((XWORD)(0xDC00 | (value & 0x3FF)));
03748 
03749                                                                               //XCHAR character = ((hi<<16) | lo);  
03750                                                                               //Add((XCHAR)character);
03751 
03752                                                                               Add((XCHAR)(0xD800 | (value >> 10)));
03753                                                                               Add((XCHAR)(0xDC00 | (value & 0x3FF)));
03754                                                                             } 
03755                                                                            else 
03756                                                                             {
03757                                                                               Add((XCHAR)value);
03758                                                                             }
03759                                                                         }                                                                       
03760                                                                     } 
03761                                                                     status = true;              
03762                                                                     break;
03763 
03764                                     }
03765                                   break;
03766   
03767       case XSTRINGCODING_UTF32  : switch(buffercoding)
03768                                     {
03769                                       case XSTRINGCODING_UNKWOWN  : { xbuffer.ResetPosition();
03770 
03771                                                                       for(XDWORD c=0; c<xbuffer.GetSize(); c++)
03772                                                                         {
03773                                                                           XBYTE character;
03774 
03775                                                                           xbuffer.Get(character);
03776                                                                           Add((XCHAR)character);
03777                                                                         }                                                                                                        
03778                                                                     }
03779                                                                     status = true;  
03780                                                                     break;    
03781 
03782                                       case XSTRINGCODING_UTF8     : ConvertFromUTF8(xbuffer);
03783                                                                     status = true;  
03784                                                                     break;
03785 
03786                                       case XSTRINGCODING_UTF16    : { xbuffer.ResetPosition();
03787 
03788                                                                       for(size_t c=0; c<xbuffer.GetSize(); c++) 
03789                                                                         {
03790                                                                           XWORD value;
03791                                                                           xbuffer.Get(value);
03792 
03793                                                                           if(value >= 0xD800 && value <= 0xDBFF && c + 1 < xbuffer.GetSize()) 
03794                                                                             {
03795                                                                               XWORD low;
03796                                                                               xbuffer.Get(low);
03797 
03798                                                                               if(low >= 0xDC00 && low <= 0xDFFF) 
03799                                                                                 {
03800                                                                                   XDWORD value2 = ((value - 0xD800) << 10) + (low - 0xDC00) + 0x10000;
03801                                                                                   Add((XCHAR)value2);
03802                                                                                   c++;
03803                                                                                 } 
03804                                                                                else 
03805                                                                                 {
03806                                                                                   Add((XCHAR)value);
03807                                                                                 }
03808                                                                             } 
03809                                                                            else 
03810                                                                             {
03811                                                                               Add((XCHAR)value);
03812                                                                             }
03813                                                                         }
03814                                                                     }
03815                                                                     status = true;  
03816                                                                     break;                                                                    
03817 
03818                                       case XSTRINGCODING_UTF32    : { xbuffer.ResetPosition();
03819 
03820                                                                       for(XDWORD c=0; c<xbuffer.GetSize(); c++)
03821                                                                         {
03822                                                                           XDWORD character;
03823 
03824                                                                           xbuffer.Get(character);
03825                                                                           Add((XCHAR)character);
03826                                                                         }                                                                                                          
03827                                                                     }
03828                                                                     status = true;
03829                                                                     break;
03830 
03831                                     }
03832                                   break;
03833     }
03834 
03835   return status;
03836 }
03837 
03838 
03852 bool XSTRING::ConvertToExchangeXBuffer(XBUFFER& xbuffer, bool addzeroatend, bool inverse)
03853 {
03854   if(!ConvertToUTF16(xbuffer)) return false;
03855 
03856   if(addzeroatend)
03857     {
03858       xbuffer.Add((XWORD)0);
03859     }
03860 
03861   if(inverse)
03862     {
03863       XWORD* data = (XWORD*)xbuffer.Get();
03864       if(data)
03865         {
03866           for(XDWORD c=0; c<(xbuffer.GetSize()/2); c++)
03867             {
03868               SWAPWORD(data[c])  
03869             }
03870         }
03871     }
03872 
03873   return true;
03874 
03875 }
03876 
03877 
03887 int XSTRING::GetSizeConvertToUTF8()
03888 {
03889   XDWORD size = 0;
03890 
03891   if(IsEmpty()) return size;
03892 
03893   int i = 0;
03894   int j = 0;
03895   int c = (int)text[i++];
03896 
03897   while(c)
03898     {
03899       if(c<0)
03900         {
03901           j++;
03902         }
03903        else
03904         {
03905           if(c<128)
03906             {
03907               j++;
03908             }
03909            else
03910             {
03911                 if(c< 2048)
03912                   {
03913                     j++;
03914                     j++;
03915                   }
03916                  else
03917                   {
03918                     if(c<65536)
03919                       {
03920                         j++;
03921                         j++;
03922                         j++;
03923                       }
03924                      else
03925                       {
03926                         j++;
03927                         j++;
03928                         j++;
03929                         j++;
03930                       }
03931                   }
03932             }
03933         }
03934 
03935       c = text[i++];
03936     }
03937 
03938   size = j;
03939 
03940   return size;
03941 }
03942 
03943 
03956 bool XSTRING::ConvertFromUTF8(XBYTE* data,XDWORD size)
03957 {
03958   XDWORD  sizeutf8  = 0;
03959   XDWORD  index     = 0;
03960   XBYTE*  ptr;
03961   XDWORD  c;
03962 
03963   c   = 0;
03964   ptr = data;
03965   while(c<size)
03966     {
03967       if(((XBYTE)*ptr)<128 || (*ptr&192)==192) sizeutf8++;
03968       ptr++;
03969       c++;
03970     }
03971 
03972   if(!sizeutf8) return false;
03973 
03974   if(sizeutf8>size)  sizeutf8 = size;
03975 
03976   if(!ReAllocBuffer(sizeutf8)) return false;
03977 
03978   c   = 0;
03979   ptr = data;
03980   while(c < size)
03981     {
03982       XBYTE b=*ptr;
03983       //Byte represents an ASCII character. Direct copy will do.
03984       if(!(b&128))
03985         {
03986           text[index]=b;
03987         }
03988        else
03989         {
03990            //Byte is the middle of an encoded character. Ignore.
03991            if((b&192)==128)
03992              {
03993                ptr++;
03994                continue;
03995              }
03996             else
03997              {
03998                //Byte represents the start of an encoded character in the range
03999                //U+0080 to U+07FF
04000                if((b&224)==192)
04001                  {
04002                    text[index]=((*ptr&31)<<6)|(ptr[1]&63);
04003                  }
04004                 else
04005                  {
04006                     //Byte represents the start of an encoded character in the range
04007                     //U+07FF to U+FFFF
04008                     if((b&240)==224)
04009                       {
04010                         text[index]=((*ptr&15)<<12)|((ptr[1]&63)<<6)|(ptr[2]&63);
04011                       }
04012                      else
04013                       {
04014                         //Byte represents the start of an encoded character beyond the
04015                         //U+FFFF limit of 16-bit integers
04016                         if((b&248)==240)
04017                           {
04018                             text[index]='?';
04019                           }
04020                       }
04021                  }
04022              }
04023         }
04024 
04025       index++;
04026       ptr++;
04027       c++;
04028 
04029       if(index>=sizeutf8) break;
04030     }
04031 
04032   return true;
04033 }
04034 
04035 
04047 bool XSTRING::ConvertFromUTF8(XBUFFER& xbuffer)
04048 {
04049   return ConvertFromUTF8(xbuffer.Get(), xbuffer.GetSize());
04050 }
04051 
04052 
04066 bool XSTRING::ConvertToUTF8(XBYTE* data, int& size, bool addzeroatend)
04067 {
04068   size = 0;
04069   if(!data) return false;
04070 
04071   if(IsEmpty()) return false;
04072 
04073   int i = 0;
04074   int j = 0;
04075   int c = (int)text[i++];
04076 
04077   while(c)
04078     {
04079       if(c<0)
04080         {
04081           data[j++]=1;
04082         }
04083        else
04084         {
04085           if(c<128)
04086             {
04087               data[j++] = (XBYTE)c;
04088             }
04089            else
04090             {
04091                 if(c< 2048)
04092                   {
04093                     data[j++]= (XBYTE)(192+ c/64);
04094                     data[j++]= (XBYTE)(128+(c&63));
04095                   }
04096                  else
04097                   {
04098                     if(c<65536)
04099                       {
04100                         data[j++]=  (XBYTE)(224+ c/4096);
04101                         data[j++]=  (XBYTE)(128+(c&4095)/64);
04102                         data[j++]=  (XBYTE)(128+(c&63));
04103                       }
04104                      else
04105                       {
04106                         data[j++]=  (XBYTE)(240+ c/262144);
04107                         data[j++]=  (XBYTE)(128+(c&262143)/4096);
04108                         data[j++]=  (XBYTE)(128+(c&4095)/64);
04109                         data[j++]=  (XBYTE)(128+(c&63));
04110                       }
04111 
04112 
04113                   }
04114             }
04115         }
04116 
04117       c = text[i++];
04118     }
04119 
04120   if(addzeroatend)
04121     {
04122       data[j]=0;     
04123     }
04124    else 
04125     {
04126       j--;
04127     }
04128  
04129   size = j;
04130 
04131   return true;
04132 }
04133 
04134 
04147 bool XSTRING::ConvertToUTF8(XBUFFER& xbuffer, bool addzeroatend)
04148 {
04149   if(IsEmpty()) return false;
04150 
04151   XDWORD sizeUTF8 = GetSizeConvertToUTF8();
04152 
04153   if(addzeroatend) 
04154     {
04155       sizeUTF8++;
04156     }
04157 
04158   xbuffer.Delete();
04159   xbuffer.Resize(sizeUTF8);
04160   xbuffer.FillBuffer();
04161 
04162   int _size  = xbuffer.GetSize();
04163 
04164   return ConvertToUTF8(xbuffer.Get(),  _size, addzeroatend);
04165 }
