04183 
04184 
04196 bool XSTRING::ConvertToUTF16(XBUFFER& xbuffer)
04197 {
04198   if(IsEmpty()) return false;
04199 
04200   bool status = false;
04201 
04202   xbuffer.Empty();
04203 
04204   switch(GetTypeCoding())
04205     {
04206       case XSTRINGCODING_UTF16  : { for(XDWORD c=0; c<size; c++)
04207                                       {
04208                                         xbuffer.Add((XWORD)text[c]);
04209                                       }
04210                                   
04211                                     status = true;  
04212                                   }
04213                                   break;  
04214   
04215       case XSTRINGCODING_UTF32  : { for(size_t c=0; c<size; c++) 
04216                                       {
04217                                         XDWORD value = text[c];
04218                                         if (value > 0xFFFF) 
04219                                           {
04220                                             value -= 0x10000;
04221                                             xbuffer.Add((XWORD)(0xD800 | (value >> 10)));
04222                                             xbuffer.Add((XWORD)(0xDC00 | (value & 0x3FF)));
04223                                           } 
04224                                          else 
04225                                           {
04226                                             xbuffer.Add((XWORD)value);
04227                                           }
04228                                       }
04229 
04230                                     status = true;  
04231                                   }                               
04232                                   break;
04233     }
04234  
04235   return status;
04236 }
