04252   return ConvertFromXBuffer(xbuffer, XSTRINGCODING_UTF32);
04253 }
04254 
04255 
04267 bool XSTRING::ConvertToUTF32(XBUFFER& xbuffer)
04268 {
04269   bool status = false;
04270 
04271   xbuffer.Empty();
04272 
04273   switch(GetTypeCoding())
04274     {
04275       case XSTRINGCODING_UTF16  : { for(size_t c=0; c<size; c++) 
04276                                       {
04277                                         XDWORD value = text[c];
04278                                         if(value >= 0xD800 && value <= 0xDBFF && c + 1 < size) 
04279                                           {
04280                                             XWORD low = text[c + 1];
04281                                             if(low >= 0xDC00 && low <= 0xDFFF) 
04282                                               {
04283                                                 value = ((value - 0xD800) << 10) + (low - 0xDC00) + 0x10000;
04284                                                 xbuffer.Add((XDWORD)value);
04285                                                 c++;
04286                                               } 
04287                                              else 
04288                                               {
04289                                                 xbuffer.Add((XDWORD)value);
04290                                               }
04291                                           } 
04292                                          else 
04293                                           {
04294                                             xbuffer.Add((XDWORD)value);
04295                                           }
04296                                       }
04297                                   }
04298                                   status = true;  
04299                                   break;
04300   
04301       case XSTRINGCODING_UTF32  : { for(XDWORD c=0; c<size; c++)
04302                                       {
04303                                         xbuffer.Add((XDWORD)text[c]);
04304                                       }
04305                                   }
04306                                   status = true;  
04307                                   break;
04308     }
04309    
04310   return status;
04311 }
