04328   XBUFFER charstr;
04329 
04330   string.Empty();
04331  
04332   (*this).ConvertToASCII(charstr);  
04333   data.Add((XBYTE*)charstr.Get(), GetSize());  
04334   
04335   string.ConvertBinaryToBase64(data);
04336 
04337   return string.GetSize()?true:false;
04338 }
