04445 bool XSTRING::ConvertFromDoubleToSpanishText(double value, bool withintegerpart, bool withdecimalpart, double& integerpart, double& decimalpart)
04446 {
04447   Empty();
04448 
04449   integerpart = Truncate(value);
04450   decimalpart = Fraction(value);
04451 
04452   XSTRING tmp;
04453 
04454   if(withintegerpart)
04455     {
04456       double _integerpart;
04457       double _decimalpart;
04458 
04459       if(integerpart == 0) Set(__L("cero"));
04460         else if(integerpart ==  1) Set(__L("uno"));
04461         else if(integerpart ==  2) Set(__L("dos"));
04462         else if(integerpart ==  3) Set(__L("tres"));
04463         else if(integerpart ==  4) Set(__L("cuatro"));
04464         else if(integerpart ==  5) Set(__L("cinco"));
04465         else if(integerpart ==  6) Set(__L("seis"));
04466         else if(integerpart ==  7) Set(__L("siete"));
04467         else if(integerpart ==  8) Set(__L("ocho"));
04468         else if(integerpart ==  9) Set(__L("nueve"));
04469         else if(integerpart == 10) Set(__L("diez"));
04470         else if(integerpart == 11) Set(__L("once"));
04471         else if(integerpart == 12) Set(__L("doce"));
04472         else if(integerpart == 13) Set(__L("trece"));
04473         else if(integerpart == 14) Set(__L("catorce"));
04474         else if(integerpart == 15) Set(__L("quince"));
04475         else if(integerpart < 20)
04476                {
04477                   Set(__L("dieci"));
04478                   tmp.ConvertFromDoubleToSpanishText(integerpart - 10, true, false, _integerpart, _decimalpart);
04479                   Add(tmp);
04480                 }
04481                else if(integerpart == 20) Set(__L("veinte"));
04482                       else if(integerpart < 30)
04483                             {
04484                               Set(__L("veinti"));
04485                               tmp.ConvertFromDoubleToSpanishText(integerpart - 20, true, false, _integerpart, _decimalpart);
04486                               Add(tmp);
04487                              }
04488                             else if(integerpart == 30) Set(__L("treinta"));
04489                             else if(integerpart == 40) Set(__L("cuarenta"));
04490                             else if(integerpart == 50) Set(__L("cincuenta"));
04491                             else if(integerpart == 60) Set(__L("sesenta"));
04492                             else if(integerpart == 70) Set(__L("setenta"));
04493                             else if(integerpart == 80) Set(__L("ochenta"));
04494                             else if(integerpart == 90) Set(__L("noventa"));
04495                             else if(integerpart < 100)
04496                                    {
04497                                      ConvertFromDoubleToSpanishText(Truncate(integerpart/10) * 10, true, false, _integerpart, _decimalpart);
04498                                      Add(__L(" y "));
04499                                      tmp.ConvertFromDoubleToSpanishText(fmod(integerpart, (double)10), true, false, _integerpart, _decimalpart);
04500                                      Add(tmp);
04501                                    }
04502                                   else if(integerpart == 100) Set(__L("cien"));
04503                                   else if(integerpart < 200)
04504                                          {
04505                                            Set(__L("ciento "));
04506                                            tmp.ConvertFromDoubleToSpanishText(integerpart - 100, true, false, _integerpart, _decimalpart);
04507                                            Add(tmp);
04508                                          }
04509                                         else if((integerpart == 200) || (integerpart == 300) || (integerpart == 400) || (integerpart == 600) || (integerpart == 800))
04510                                                {
04511                                                  ConvertFromDoubleToSpanishText(Truncate(integerpart / 100), true, false, _integerpart, _decimalpart);
04512                                                  Add(__L("cientos"));
04513                                                }
04514                                               else if(integerpart == 500) Set(__L("quinientos"));
04515                                               else if(integerpart == 700) Set(__L("setecientos"));
04516                                               else if(integerpart == 900) Set(__L("novecientos"));
04517                                               else if(integerpart < 1000)
04518                                                    {
04519                                                      ConvertFromDoubleToSpanishText(Truncate(integerpart / 100) * 100, true, false, _integerpart, _decimalpart);
04520                                                      Add(__L(" "));
04521                                                      tmp.ConvertFromDoubleToSpanishText(fmod(integerpart, (double)100), true, false, _integerpart, _decimalpart);
04522                                                      Add(tmp);
04523                                                    }
04524                                                   else if(integerpart == 1000) Set(__L("mil"));
04525                                                   else if(integerpart < 2000)
04526                                                         {
04527                                                           Set(__L("mil "));
04528                                                           tmp.ConvertFromDoubleToSpanishText(fmod(integerpart, (double)1000), true, false, _integerpart, _decimalpart);
04529                                                           Add(tmp);
04530                                                         }
04531                                                       else if(integerpart < 1000000)
04532                                                               {
04533                                                                 ConvertFromDoubleToSpanishText(Truncate(integerpart / 1000), true, false, _integerpart, _decimalpart);
04534                                                                 Add(__L(" mil"));
04535                                                                 if((fmod(integerpart, (double)1000)) > 0)
04536                                                                   {
04537                                                                     Add(" ");
04538                                                                     tmp.ConvertFromDoubleToSpanishText(fmod(integerpart, (double)1000), true, false, _integerpart, _decimalpart);
04539                                                                     Add(tmp);
04540                                                                   }
04541                                                               }
04542                                                             else if(integerpart == 1000000) Set(__L("un millon"));
04543                                                             else if(integerpart < 2000000)
04544                                                                     {
04545                                                                       Set(__L("un millon"));
04546                                                                       tmp.ConvertFromDoubleToSpanishText(fmod(integerpart, (double)1000000), true, false, _integerpart, _decimalpart);
04547                                                                       Add(tmp);
04548                                                                     }
04549                                                                    else if(integerpart < 1000000000000LL)
04550                                                                           {
04551                                                                              ConvertFromDoubleToSpanishText((integerpart / 1000000), true, false, _integerpart, _decimalpart);
04552                                                                              Add(__L(" millones"));
04553                                                                              if((integerpart - Truncate(integerpart / 1000000) * 1000000) > 0)
04554                                                                                {
04555                                                                                  Add(__L(" "));
04556                                                                                  tmp.ConvertFromDoubleToSpanishText(integerpart - Truncate(integerpart / 1000000) * 1000000, true, false, _integerpart, _decimalpart);
04557                                                                                  Add(tmp);
04558                                                                                }
04559                                                                           }
04560                                                                          else
04561                                                                           {
04562                                                                             if(integerpart == 1000000000000LL) Set(__L("un billon"));
04563                                                                               else if(integerpart < 2000000000000LL)
04564                                                                                 {
04565                                                                                   Set(__L("un billon"));
04566                                                                                   tmp.ConvertFromDoubleToSpanishText(Truncate(integerpart / 1000000000000LL) * 1000000000000LL, true, false, _integerpart, _decimalpart);
04567                                                                                   Add(tmp);
04568                                                                                 }
04569                                                                                else
04570                                                                                 {
04571                                                                                    ConvertFromDoubleToSpanishText(Truncate(integerpart / 1000000000000LL), true, false, _integerpart, _decimalpart);
04572                                                                                    Add(__L(" billones"));
04573 
04574                                                                                    if((integerpart - Truncate(integerpart / 1000000000000LL) * 1000000000000LL) > 0)
04575                                                                                      {
04576                                                                                        Add(__L(" "));
04577                                                                                        tmp.ConvertFromDoubleToSpanishText(integerpart - Truncate(integerpart / 1000000000000LL) * 1000000000000LL, true, false, _integerpart, _decimalpart);
04578                                                                                        Add(tmp);
04579                                                                                      }
04580                                                                                 }
04581                                                                           }
04582       if(!integerpart) return false;
04583     }
04584 
04585   if(withdecimalpart)
04586     {
04587       double _integerpart;
04588       double _decimalpart;
04589 
04590       double ipart = Truncate(decimalpart*100);
04591       double dpart = Fraction(decimalpart*100);
04592 
04593       if(dpart>=0.9f) ipart++;
04594 
04595       if(ipart)
04596         {
04597           if(withintegerpart) Add(__L(" con "));
04598           tmp.ConvertFromDoubleToSpanishText(ipart, true, false, _integerpart, _decimalpart);
04599           Add(tmp);
04600 
04601         } else return false;
04602     }
04603 
04604   return true;
04605 }
