04617 bool XSTRING::Swab()
04618 {
04619   if(IsEmpty()) return false;
04620 
04621   for(int c=0;c<(int)GetSize();c+=2)
04622     {
04623       XCHAR data;
04624 
04625       data      = text[c+1];
04626       text[c+1] = text[c];
04627       text[c]   = data;
04628     }
04629 
04630   return true;
04631 }
